import React,{useEffect,useState} from 'react';
import {getAllRepository} from '../domains/git/GitService';
import Spinner from '../components/UI/Spinner';
import GitRepositoryGrid from '../components/git/repositories/GridComponent';
import ErrorPage from '../components/UI/ErrorPage';


export default function GitContainer () {


  const [gitList, setGitList] = useState([]);

  const [isLoading, setLoading] = useState({
    value: true,
  });

  const [error, setError] = useState({
    value: null,
  });
  const setErrorState = (newValue) => {    
    setError({ ...error, value: newValue })
  }

  useEffect(() => {  

    async function fetchData(){

      try{
        const repos = await getAllRepository();  
        setGitList(repos.data);        
      }
      catch(e){                
        setErrorState(e);
      }
      finally{        
        setLoading(false);
      }

    }

    fetchData();

  },[gitList])  

  if(gitList.length > 0){
    return (<GitRepositoryGrid data={gitList}/>);
  }

  if(isLoading){        
    return(<Spinner loading={isLoading} />);  
  }  

  if(error !== null){      
      return (<ErrorPage error={error.value} />);
  }

  return(<div>"No data found"</div>);

}
