import {getAll} from '../shared/Api';

const basePath = 'https://api.github.com';
const pathRepository = '/user/repos';
const url = basePath + pathRepository;    

export async function  getAllRepository() {    

    try {
        const repos = await getAll(url);        
        return repos;
    } 
    catch(error) {
        throw error;        
    }
}
